							local TargetHRP = v:FindFirstChild("HumanoidRootPart")
							if TargetHRP and HumanoidRootPart then
								local mag = (HumanoidRootPart.Position - TargetHRP.Position).magnitude
								if mag < TargetDistance then
									TargetDistance = mag
									Target = v
								end
