	local function checkcombo(t)
		if not checkingcombo then
			checkingcombo = true
			local truecombo = t:gsub(" ", "")
			local alreadyran = false
			for _, v in pairs(combos) do
				if not alreadyran then
					local schain = string.lower(v.chain)
					if truecombo == schain and not alreadyran then
						alreadyran = true
						displaying = true
						ct("> " .. string.upper(v.name) .. " <")
						task.wait(.2)
						displaying = false
						clear()
						local ye = v.func()
						lastmove = v.func
						if isblaze then
							print("blaze true")
							if not ye and ye ~= "blaze" then
								print(ye)
								removeblaze()
							end
						end
						doingmove = false
					end
				end
			end
			checkingcombo = false
		end
	end

