	function me(plr)
		local c = plr
		del(c:FindFirstChild("Shirt"))
		del(c:FindFirstChild("Pants"))
		for _, v in pairs(c:GetChildren()) do
			if v:IsA("Accessory") or v.Name == "CharacterMesh" then
				del(v)
			end
		end
		del(c:FindFirstChildWhichIsA("BodyColors"))
		del(c.Head.face)
		del(c.Head.Mesh)
		del(c:FindFirstChild("HPScript"))
		del(c:FindFirstChild("Stats"))
		for _, v in pairs(c.Head:GetChildren()) do
			if v:IsA("BillboardGui") then
				del(v)
			end
		end
		local cc = game.Players.LocalPlayer.Character
		for _, v in pairs(cc:GetChildren()) do
			if v:IsA("Accessory") then
				ok(v, c)
			end
		end
