local classinfo = infomodule

local function setup()
	local centerish = Vector3.new(2, 146, 920)
	local ispawn = lobby.IntroSpawn

	for _, v in pairs(lobby["Room - Role Selection"].Terrain:GetChildren()) do 
		local mag = math.abs((centerish - v.Position).Magnitude)

		if mag <= 30 then 
			v:Destroy() 
		end 
	end

	for _, v in pairs(ispawn:GetChildren()) do 
		if v.Name == 'Part' then 
			v:Destroy()
		end 
	end
end



local function class(c)
	local curclass = c:WaitForChild('CurrentClass')

	repeat 
		task.wait(.5)
	until curclass.Value ~= 'none'

	local theclass = curclass.Value
	local info = classinfo[string.lower(theclass)]
	if info then 
		info() 
	else 
		print('no info found on it')
	end
end

local char = plr.Character 

plr.CharacterAdded:Connect(function(char) 
	class(char)
end)

setup()

class(char)
